import React, { useState } from 'react';
import { View, Text, TextInput, Button, FlatList, StyleSheet } from 'react-native';
import { searchShows } from '../api/apiService';
import { SearchResult } from '../types/apiTypes'; // Make sure this path is correct

const SearchScreen = () => {
    const [query, setQuery] = useState('');
    const [results, setResults] = useState<SearchResult[]>([]); // Explicitly set the type
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState<string | null>(null);

    const handleSearch = () => {
        setLoading(true);
        setError(null);
        searchShows(query)
            .then(data => {
                setResults(data);
            })
            .catch(err => {
                setError(err.message);
            })
            .finally(() => {
                setLoading(false);
            });
    };

    return (
        <View style={styles.container}>
            <Text style={styles.title}>Search Shows</Text>
            <TextInput
                style={styles.input}
                placeholder="Enter show name"
                value={query}
                onChangeText={setQuery}
            />
            <Button title="Search" onPress={handleSearch} />
            {loading && <Text>Loading...</Text>}
            {error && <Text style={styles.error}>Error: {error}</Text>}
            <FlatList
                data={results}
                keyExtractor={(item) => item.show.id.toString()}
                renderItem={({ item }) => (
                    <View style={styles.resultItem}>
                        <Text>{item.show.name}</Text>
                    </View>
                )}
            />
        </View>
    );
};

const styles = StyleSheet.create({
    container: { padding: 20 },
    title: { fontSize: 24, fontWeight: 'bold', marginBottom: 10 },
    input: { borderWidth: 1, padding: 8, marginBottom: 10 },
    error: { color: 'red' },
    resultItem: { marginVertical: 5, padding: 10, borderWidth: 1, borderColor: '#ddd' },
});

export default SearchScreen;
